import React, { useState } from 'react';
import './card.css';

import { useDispatch, useSelector } from 'react-redux';

const Card = (props) => {
    const showState = useSelector(state => state.showToggler);
    const dispetch = useDispatch();

    const onToggleHandler = () => {
        dispetch({ type: 'show' });
    }
    const [showButton, setShowButton] = useState(false);
    const showAddToCartButton = () => {
        setShowButton(true);
    }
    return (
        <React.Fragment>
            <div className="card" >
            <div className="card-body" >
                <img className="card-img-top" src={props.imgsrc} alt="Cant load" />
                    <h6 className="card-text" onMouseOver={onToggleHandler}>{props.brand}</h6>
                    <p className="card-title">{props.detail}</p>

                    {showState &&
                    <div>
                        <p>Select size</p>
                        <div className="size">
                        <input type="button" value="38" onClick={showAddToCartButton}/>
                        <input type="button" value="39"/>
                        <input type="button" value="40"/>
                        <input type="button" value="42"/>
                        <input type="button" value="44"/></div>
                     {showButton ? <button>Add to Cart</button> : <div className="availabilityMessage"> Sizes: XS,S,M,L,XL,XXL </div>}</div>}
                    <p className="card-price">{props.price}</p>



                </div>
            </div>
        </React.Fragment>
    )
};

export default Card;
