import React, { useState } from 'react';
import Header from './components/Headers/Header';
import Refferal from './components/RefferalLine/Refferal';
import UrlLine from './components/UrlLine/UrlLine';
import ProductCount from './components/ProductCount/ProductCount';
import Filter from './components/filter/Filter';
import Product from './components/Product/Product';
import data from './constants/data';
import MapCard from './components/Card/MapCard';
import CartItem from './components/Headers/cart/CartItem';

const Main = (props) => {
    const [showFullData,setShowFullData] = useState(true);
    const [showcartItem,setShowCartItem] = useState(false);

    const showInitialData = () => {
        <div className="displayCard"> {data.map(MapCard)} </div>
    }
    const hideInitialData = () => {
        setShowFullData(false)
    }

    return (
        <React.Fragment>
            {showcartItem && <CartItem/>}
            <Header />
            <Refferal />
            <UrlLine />
            <ProductCount />
            <Filter onHide={hideInitialData}/>
            <Product/>
        </React.Fragment>
    )
}

export default Main;
