import React from 'react';
import './card.css';
import { useState } from 'react';

const Card = (props) => {
const [showMessage,setShowMessage] = useState(false);

const showAvailabilityHandler = () =>{
    setShowMessage(true);
}

    return(
        <React.Fragment>
             <div className="card" onMouseMove={showAvailabilityHandler}>
                <img className="card-img-top" src={props.imgsrc} alt="Cant load" />
                <div className="card-body">
                <h6 className="card-text">{props.brand}</h6>
                <p className="card-title">{props.detail}</p>   
                <p className="card-price">{props.price}</p>
                {showMessage ? <p className="availabilityMessage">Hurry up! only 3 left</p> : setShowMessage(false)}
                <div className="displayButtons"> <button className="btn btn-primary">Buy Now</button> <button className="btn btn-primary">Add To Cart</button> </div>

                </div>
            </div>
        </React.Fragment>
    )
};

export default Card;
